mapper.select:
select

  distinct

'true' as QUERYID,

from gn_subject




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

select 

from gn_subject
where tenant_id = #{tenantId,jdbcType=VARCHAR}
  and industry_code = #{industryCode,jdbcType=VARCHAR}
  and subject_id = #{subjectId,jdbcType=BIGINT}

mapper.delete:
delete from gn_subject
where tenant_id = #{tenantId,jdbcType=VARCHAR}
  and industry_code = #{industryCode,jdbcType=VARCHAR}
  and subject_id = #{subjectId,jdbcType=BIGINT}

mapper.update:
update gn_subject
set subject_name = #{subjectName,jdbcType=VARCHAR},
  subject_desc = #{subjectDesc,jdbcType=VARCHAR},
  subject_type = #{subjectType,jdbcType=VARCHAR},
  unit_name = #{unitName,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  create_oper_id = #{createOperId,jdbcType=VARCHAR},
  chnl_id = #{chnlId,jdbcType=VARCHAR},
  tax_type = #{taxType,jdbcType=INTEGER}
where tenant_id = #{tenantId,jdbcType=VARCHAR}
  and industry_code = #{industryCode,jdbcType=VARCHAR}
  and subject_id = #{subjectId,jdbcType=BIGINT}
